mapper.select:
select * from comment_view
where performance_records_id = #{recordId}
order by reg_date

mapper.update:
update comment
set 
content=#{comment.content}
where id=#{comment.id}

mapper.select:
select *
from comment_view
where coalesce(choice_id, free_challenge_id, group_start_id) = #{challengeId}
