mapper.update:
update performance_records
set achv_quantity = achv_quantity + 1

where free_challenge_id = #{FCId}
and round = (
    select max(round)
    from performance_records
    where free_challenge_id = #{FCId}
    )


    where choice_id = #{CHId}
    and round = (
    select max(round)
    from performance_records
    where choice_id = #{CHId}
    )


    where group_start_id = #{GSId}
    and round = (
    select max(round)
    from performance_records
    where group_start_id = #{GSId}
    )

mapper.select:
select achv_quantity
from performance_records

    where free_challenge_id = #{FCId}
    and round = (
    select max(round)
    from performance_records
    where free_challenge_id = #{FCId}
    )


    where choice_id = #{CHId}
    and round = (
    select max(round)
    from performance_records
    where choice_id = #{CHId}
    )


    where group_start_id = #{GSId}
    and round = (
    select max(round)
    from performance_records
    where group_start_id = #{GSId}
    )
