mapper.update:
update performance_records
set achv_quantity = achv_quantity + 1

where free_challenge_id = #{FCId}
and round = (
    select max(round)
    from performance_records
    where free_challenge_id = #{FCId}
    )


    where choice_id = #{CHId}
    and round = (
    select max(round)
    from performance_records
    where choice_id = #{CHId}
    )


    where group_start_id = #{GSId}
    and round = (
    select max(round)
    from performance_records
    where group_start_id = #{GSId}
    )

mapper.select:
select achv_quantity
from performance_records

    where free_challenge_id = #{FCId}
    and round = (
    select max(round)
    from performance_records
    where free_challenge_id = #{FCId}
    )


    where choice_id = #{CHId}
    and round = (
    select max(round)
    from performance_records
    where choice_id = #{CHId}
    )


    where group_start_id = #{GSId}
    and round = (
    select max(round)
    from performance_records
    where group_start_id = #{GSId}
    )

select * 
from performance_records

    where free_challenge_id=#{FCId}


    where choice_id=#{CHId}


    where group_start_id=#{GSId}

mapper.update:
update performance_records
set
impression = #{record.impression},
achv_quantity = #{record.achvQuantity},
image = #{record.image}
where id = #{record.id}

update performance_records

set result='성공',reg_date=#{currentTime}


set result='진행중'

where id=#{record.id}

update performance_records
set result='실패'
where id=#{record.id}

update performance_records
set 
result='성공',
reg_date = #{record.regDate}
where id=#{record.id}
